def splitCMDS(commands):
    r''' 匹配大括号里面的内容
    method-1: '{[^}]+}'
    method-2: /{[\S\s]+}/ 或者 /{(.|\r\n)+}/
    注：\S\s可是微软文档中对\n匹配的标准用法
    '''

    pattern = '{[^}]+}'
    regex = re.compile(pattern)
    return regex.findall(commands)


def fuzzyFinder(user_input, collection):
    '''ref https://www.cnblogs.com/weiman3389/p/6047017.html

    '''
    suggestions = []
    pattern = '.*?'.join(user_input) # Converts 'djm' to 'd.*?j.*?m'
    # 将正则修改为’非贪婪匹配non-greedy’,加了一个问号?, (*,+,?,{n},{n,}, or {n,m})。中文字符也可以完美匹配!
    # TODO: How to ignore differences in Capitalization 忽略大小写
    regex = re.compile(pattern)     # Compiles a regex.
    for item in collection:
        match = regex.search(item)  # Checks if the current item matches the regex.
        if match:
            # 推荐列表做成了三元tuple的列表的形式，
            # 即推荐列表中的每一个元素为一个三元tuple，
            # 而该三元tuple的第一个值为匹配到的内容的长度、第二个值为匹配到的起始位置、第三个值为对应的文件名，然后按照匹配长度和起始位置进行排序并返回。
            suggestions.append((len(match.group()), match.start(), item))
    return [x.replace('"','') for _, _, x in sorted(suggestions)]  # eval will change set-sorted, 


if __name__ == '__main__':
    if len(argv) > 1:
        cmds = readFile()
        cmdsList = splitCMDS(cmds)
        search = argv[1]
        print(fuzzyFinder(search, cmdsList))
    else:
        print("""
        使用说明：gcmd searched_string -- 模糊查询给定的字符并排序, 英文区分大小写
        使用举例：gcmd Point -- 模糊查询P*o*i*t
                  gcmd point -- 模糊查询p*o*i*t
                  gcmd ZPE -- 模糊查询Z*P*E""")

